import axios from "axios";

const apiUrl = "https://api.openai.com/v1/threads";
const headers = {
  Authorization: `Bearer ${process.env.REACT_APP_GPT_KEY}`,
  "Content-Type": "application/json",
  "OpenAI-Beta": "assistants=v2",
};
let sessionId = null;

export const createdThread = async (prompt) => {
  const payload = {
    model: "gpt-4",
    messages: [{ role: "user", content: prompt }],
  };
  const response = await axios.post(apiUrl, payload, headers);
  sessionId = response.data.id;
  return response.data;
};

export const moreThread = async (prompt) => {
  const payload = {
    model: "gpt-4",
    messages: [{ role: "user", content: prompt }],
  };
  const response = await axios.get(`${apiUrl}/${threadId}`, headers);
  return response.data; // Returns the thread object
};

